import React, { useEffect, useState } from "react";
import { MapContainer, Marker, Popup, TileLayer, Tooltip, useMap } from 'react-leaflet'

import "leaflet/dist/leaflet.css"; // 설정안하면 조각조각 나옴
import { L, icon } from "leaflet";

import LinkToMap from "../LinkToMap";

const LOCATION = [36.32787631122742, 127.42585033518178];
const Icon = icon({
    iconUrl: "/images/marker-icon.png"
  });

const mapList = [
    {lat: LOCATION[0], lng:LOCATION[1], title: '처음'},
    {lat: 36.31424840000091, lng:127.41274601574581, title: '성심당'},
    {lat: 36.32420013562803, lng:127.40640003481619, title: '코스트코'},
    {lat: 36.356766196549856, lng:127.3808515099755, title: '정부청사역'},
]


const SearchLeafletMap = () => {
    const [place, setPlace] = useState(mapList[0]);
    const [index, setIndex] = useState(0);

    const handleOnClick = (e) => {
        e.preventDefault();

        const index = e.target.value;

        setIndex(index);
        setPlace(mapList[index]);
    }

    useEffect(() => {
        L.map("leafletMap", {
            center: [place.lat, place.lng],
            zoom: 16,
            layers: [
              L.tileLayer("http://{s}.tile.osm.org/{z}/{x}/{y}.png", {
                attribution:
                  '&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
              })
            ]
          });
    }, [place]);

    return (
        <div >
            <LinkToMap />
            <h1>Leaflet 지도 검색</h1>

            <div style={{position: 'relative', width:'1000px', height:'500px'}}>
                <MapContainer center={[place.lat, place.lng]} zoom={16} scrollWheelZoom={true} style={{ width: "1000px", height: "500px" }} >
                    <TileLayer
                    attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                    url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                    />
                    <Marker position={[place.lat, place.lng]} icon={Icon} url={"/images/marker-icon.png"}>
                    <Popup>
                        여기는 {place.title}입니다.
                    </Popup>
                    <Tooltip>{place.title}</Tooltip>
                    </Marker>
                </MapContainer>
            </div>

            <div id="leafletMap" style={{position: 'relative', width:'1000px', height:'500px'}}></div>

            <div>
                <button value={1} onClick={handleOnClick}>성심당</button>
                <button value={2} onClick={handleOnClick}>코스트코</button>
                <button value={3} onClick={handleOnClick}>정부청사역</button>
            </div>

        </div>
    )
}

export default SearchLeafletMap;
