import React, { useEffect } from "react";

const {kakao} = window;

const KakaoMap = ({searchPlace}) => {

    useEffect(() => {
        var infoWindow = new kakao.maps.InfoWindow({zindex : 1});
        const container = document.getElementById('myMap');
        const options = {
            center : new kakao.maps.LatLng(36.32787631122742, 127.42585033518178),
            level : 3
        };
        const map = new kakao.maps.Map(container, options);

        const ps = new kakao.maps.services.Places();
        ps.keywordSearch(searchPlace, searchPlaceCB);

        const searchPlaceCB = (data, status, pagination) => {
            if (status === kakao.maps.services.Status.OK) {
                let bounds = new kakao.maps.LatLngBounds();

                for (let i = 0; i < data.length; i++) {
                    displayMarker(data[i]);
                    bounds.extend(new kakao.maps.LatLng(data[i].x, data[i].y));
                }
            }
        }

        const displayMarker = (place) => {
            let marker = new kakao.maps.Marker({
                map: map,
                position: new kakao.maps.LatLng(place.x, place.y)
            });
            
            kakao.maps.event.addListener(marker, 'click', () => {
                infoWindow.setContent(`<div style="padding:5px;font-size:12px;">${place.place_name}</div>`);
                infoWindow.open(map, marker);
            })
        }


    }, [searchPlace]);


    return (
        <div id="myMap" style={{width:'1000px', height:'800px'}}>
            map!
        </div>
    )
}

export default KakaoMap;
