import React, { useState } from "react";
// import NaverMap from "./NaverMap";
import { Container as MapDiv, NaverMap, Marker } from "react-naver-maps";
import SeachBox from "./SearchBox";

const LOCATION = {
    x : 127.42585033518178,
    y : 36.32787631122742
};

const SearchNaverMap = () => {
    const [searchResult, setSearchResult] = useState(null);

    const handleSearchSubmit = async (value) => {
      if (!value) return;

      try {
        const searchResult = await naver.maps.Service.searchAddrToCoord({
          query: searchValue,
        });
    
        setSearchResult(searchResult.result.items[0]);
      } catch (e) {
        console.error(e);
      }
    }

    console.log(searchResult);
    
    return (
      <MapDiv style={{width:'1000px', height:'500px'}}>
        <NaverMap
          style={{ width: '100%', height: '100%' }}
          defaultCenter={{ lat: LOCATION.y, lng: LOCATION.x }}
          defaultZoom={17}
        >
        <Marker position={{ lat: LOCATION.y, lng: LOCATION.x }} />
        <SeachBox handleSearchSubmit={handleSearchSubmit} />

        {searchResult ? (
          <Marker
            position={{ lat: searchResult.y, lng: searchResult.x }}
          />
        ) : null}
        </NaverMap>
      </MapDiv>
    )
}

export default SearchNaverMap;
