import React, { useState } from "react";
import { Container as MapDiv, NaverMap, Marker, RenderAfterNavermapsLoaded } from "react-naver-maps";

import '../css/naverMap.css';

const LOCATION = {
    x : 127.42585033518178,
    y : 36.32787631122742
};

const {naver} = window;

const SearchNaverMap = () => {
    const [inputVal, setInputVal] = useState('');
    const [lat, setLat] = useState(LOCATION.y);
    const [lng, setLng] = useState(LOCATION.x);


    const infoWindow = new naver.maps.InfoWindow({anchorSkew : true});

    const searchAddressToCoordinate = (e) => {
        e.preventDefault();

        if (inputVal === '') {
            return alert('입력해주새요!');
        }

        console.log(inputVal);

        infoWindow.close();

        naver.maps.Service.geocode({query : inputVal}, (status, response) => {
            if (status === naver.maps.Service.Status.ERROR) {
                return alert('검색 결과 중 오류가 발생했습니다.');
            }
            if (response.v2.meta.totalCount === 0) {
                return alert('검색 결과가 존재하지 않습니다.');
            }

            alert("검색!");
        });
    }
  
    return (
        <RenderAfterNavermapsLoaded
            submodules={['geocoder']}
        >
      <MapDiv style={{position: 'relative', width:'1000px', height:'500px'}}>
        <div className="option"
          style={{
            position: 'absolute',
            top: 0,
            left: 0,
            zIndex: 1000,
            padding: 5,
          }}
        >
            <form onSubmit={searchAddressToCoordinate}>
                <input type="text" 
                    placeholder="도로명 주소를 입력하세요." 
                    value={inputVal}
                    onChange={(e) => setInputVal(e.target.value)}
                />
                <button type="submit">검색</button>
            </form>
        </div>
        <NaverMap
          style={{ width: '100%', height: '100%' }}
          defaultCenter={{ lat, lng }}
          defaultZoom={17}
        >
        </NaverMap>
      </MapDiv>
      </RenderAfterNavermapsLoaded>
    )
}

export default SearchNaverMap;
