import React, { useState } from "react";
// import NaverMap from "./NaverMap";
import { Container as MapDiv, NaverMap, Marker } from "react-naver-maps";

import { search } from 'react-icons-kit/fa/search';
import Icon from 'react-icons-kit';
import SeachBox from "./SearchBox";

const LOCATION = {
    x : 127.42585033518178,
    y : 36.32787631122742
};

const SearchNaverMap = () => {
    const [searchValue, setSearchValue] = useState('');
    const [searchResult, setSearchResult] = useState(null);
    

    const {naver} = window;

    const handleSearchSubmit = async (value) => {
      if (!value) return;
      setSearchResult(value);

      const result = await naver.maps.Service.geocode({
        address: value,
      }, searchPlaceCB);

      function searchPlaceCB(status, response) {
        if (status === naver.maps.Service.Status.ERROR) {
          if (!address) {
            return alert('Geocode Error, Please check address');
          }
          return alert('Geocode Error, address:' + address);
        }
    
        if (response.v2.meta.totalCount === 0) {
          return alert('No result.');
        }

        var item = response.result.items[0],
          addrType = item.isRoadAddress ? '[Road name address]' : '[Land-lot numbers]',
          point = new naver.maps.Point(item.point.x, item.point.y);
        
        console.log(item);
      }
      
    }
    
    return (
      <MapDiv style={{width:'1000px', height:'500px'}}>
        <NaverMap
          style={{ width: '100%', height: '100%' }}
          defaultCenter={{ lat: LOCATION.y, lng: LOCATION.x }}
          defaultZoom={17}
        >
        <Marker position={{ lat: LOCATION.y, lng: LOCATION.x }} />
        <div
            style={{
              position: 'absolute',
              top: '10px',
              right: '10px',
              width: '300px',
              height: '40px',
              borderRadius: '4px',
              border: '1px solid #d9d9d9',
              fontSize: '16px',
              paddingLeft: '36px',
              zIndex: '100',
              backgroundColor: 'lightgrey'
            }}
        >
            <input
            type="text"
            placeholder="장소 검색"
            value={searchValue}
            onChange={(e) => setSearchValue(e.target.value)}
            onKeyPress={(e) => {
                if (e.key === 'Enter') {
                    handleSearchSubmit(searchValue);
                }
            }}
            style={{ width: '100%', height: '100%', outline: 'none', border: 'none' }}
            />
            <Icon icon={search} size={20} style={{position: 'absolute', top: '10px', left: '10px', color: '#aaa' }} />
        </div>

        {searchResult ? (
          <Marker
            position={{ lat: searchResult.y, lng: searchResult.x }}
          />
        ) : null}
        </NaverMap>
      </MapDiv>
    )
}

export default SearchNaverMap;
