import React, { useEffect, useState } from "react";
import { Container as MapDiv, NaverMap, Marker } from "react-naver-maps";

const LOCATION = {
    x : 127.42585033518178,
    y : 36.32787631122742
};

const SearchNaverMap = () => {
    const {naver} = window;
    const infoWindow = new naver.maps.InfoWindow({anchorSkew : true});

    const [address, setAddress] = useState('');

    useEffect(() => {

        const location = new naver.maps.LatLng(LOCATION.y, LOCATION.x);

        const options = {
            center : location,
            zoom : 16,
            zoomControl : true,
            zommControlOptions: {position : naver.maps.Position.TOP_RIGHT}
        };
        const map = new naver.maps.Map('naverMap', options);

        new naver.maps.Marker({
            position: location,
            map: map
        });


    }, []);

    


    return (
        <MapDiv style={{position: 'relative', width:'1000px', height:'500px'}}>
          <div
            style={{
              position: 'absolute',
              top: 0,
              left: 0,
              zIndex: 1000,
              padding: 5,
            }}
          >
            <form>
                <input type="findAdress" placeholder="도로명 주소를 입력하세요" />
                <button type="submit"></button>
            </form>
          </div>
          <NaverMap
            style={{ width: '100%', height: '100%' }}
            defaultCenter={{ lat: LOCATION.y, lng: LOCATION.x }}
            defaultZoom={17}
          >
          <Marker position={{ lat: LOCATION.y, lng: LOCATION.x }} />
          </NaverMap>
        </MapDiv>
    )
}

export default SearchNaverMap;
