import React from "react";
import { MapContainer, Marker, Popup, TileLayer, useMap } from 'react-leaflet'

import "leaflet/dist/leaflet.css"; // 설정안하면 조각조각 나옴
import { icon } from "leaflet";

import LinkToMap from "../LinkToMap";

const LOCATION = [36.32787631122742, 127.42585033518178];
const Icon = icon({
    iconUrl: "/images/marker-icon.png"
  });

const SearchLeafletMap = () => {

    return (
        <div >
            <LinkToMap />
            <h1>Leaflet 지도 검색</h1>

            <div style={{position: 'relative', width:'1000px', height:'500px'}}>
                <MapContainer center={LOCATION} zoom={16} scrollWheelZoom={true} style={{ width: "1000px", height: "500px" }} >
                    <TileLayer
                    attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                    url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                    />
                    <Marker position={LOCATION} icon={Icon} url={"/images/marker-icon.png"}>
                    <Popup>
                        A pretty CSS3 popup. <br /> Easily customizable.
                    </Popup>
                    </Marker>
                </MapContainer>
            </div>

        </div>
    )
}

export default SearchLeafletMap;
