import React, { useState } from "react";
import { Container as MapDiv, NaverMap, Marker } from "react-naver-maps";

import '../css/naverMap.css';

const LOCATION = {
    x : 127.42585033518178,
    y : 36.32787631122742
};

const SearchNaverMap = () => {
    const [inputVal, setInputVal] = useState('');
    const infoWindow = new naver.maps.InfoWindow({anchorSkew : true});

    const searchAddressToCoordinate = (e) => {
        e.preventDefault();

        if (inputVal === '') {
            alert('입력해주새요!');
        }

        infoWindow.close();
    }
  
    return (
      <MapDiv style={{position: 'relative', width:'1000px', height:'500px'}}>
        <div className="option"
          style={{
            position: 'absolute',
            top: 0,
            left: 0,
            zIndex: 1000,
            padding: 5,
          }}
        >
            <form onSubmit={searchAddressToCoordinate}>
                <input type="text" 
                    placeholder="도로명 주소를 입력하세요." 
                    value={inputVal}
                    onChange={(e) => setInputVal(e.target.value)}
                />
                <button type="submit">검색</button>
            </form>
        </div>
        <NaverMap
          style={{ width: '100%', height: '100%' }}
          defaultCenter={{ lat: LOCATION.y, lng: LOCATION.x }}
          defaultZoom={17}
        >
        {/* <Marker position={{ lat: LOCATION.y, lng: LOCATION.x }} /> */}
        </NaverMap>
      </MapDiv>
    )
}

export default SearchNaverMap;
