import React, { useState } from "react";
// import NaverMap from "./NaverMap";
import { Container as MapDiv, NaverMap, Marker, useNavermaps } from "react-naver-maps";

const LOCATION = {
    x : 127.42585033518178,
    y : 36.32787631122742
};

const SearchNaverMap = () => {
  // const {naver} = window;

  const navermaps = useNavermaps();
  const [mapTypeId, setMapTypeId] = useState(navermaps.MapTypeId.NORMAL);
  const buttons = [
    {
      typeId: navermaps.MapTypeId.NORMAL,
      text: '일반지도',
    },
    {
      typeId: navermaps.MapTypeId.TERRAIN,
      text: '지형도',
    },
    {
      typeId: navermaps.MapTypeId.SATELLITE,
      text: '위성지도',
    },
    {
      typeId: navermaps.MapTypeId.HYBRID,
      text: '겹쳐보기',
    },
  ];
  
    return (
      <MapDiv style={{position: 'relative', width:'1000px', height:'500px'}}>
        <div
          style={{
            position: 'absolute',
            top: 0,
            left: 0,
            zIndex: 1000,
            padding: 5,
          }}
        >
          {buttons.map((btn) => {
            return (
              <button
                key={btn.typeId}
                style={{
                  // backgroundColor: '#fff',
                  border: 'solid 1px #333',
                  outline: '0 none',
                  borderRadius: '5px',
                  boxShadow: '2px 2px 1px 1px rgba(0, 0, 0, 0.5)',
                  margin: '0 5px 5px 0',
                  backgroundColor:
                    btn.typeId === mapTypeId ? '#2780E3' : 'white',
                  color: btn.typeId === mapTypeId ? 'white' : 'black',
                }}
                onClick={() => {
                  setMapTypeId(btn.typeId)
                }}
              >
                {btn.text}
              </button>
            )
          })}
        </div>
        <NaverMap
          style={{ width: '100%', height: '100%' }}
          defaultCenter={{ lat: LOCATION.y, lng: LOCATION.x }}
          defaultZoom={17}
          mapTypeId={mapTypeId}
        >
        <Marker position={{ lat: LOCATION.y, lng: LOCATION.x }} />
        </NaverMap>
      </MapDiv>
    )
}

export default SearchNaverMap;
