import React, { useEffect } from "react";



const NaverMap = () => {

    useEffect(() => {
        const {naver} = window;

        const location = new naver.maps.LatLng(36.32787631122742, 127.42585033518178);

        const options = {
            center : location,
            zoom : 16,
            zoomControl : true,
            zommControlOptions: {position : naver.maps.Position.TOP_RIGHT}
        };
        const map = new naver.maps.Map('naverMap', options);

        new naver.maps.Marker({
            position: location,
            map: map
        });

    }, []);

    const btnClick = (e) => {
        e.preventDefault();

        var mapTypeId = this.id;
    
        if (map.getMapTypeId() !== naver.maps.MapTypeId[mapTypeId]) {
            map.setMapTypeId(naver.maps.MapTypeId[mapTypeId]); // 지도 유형 변경하기
    
            btns.removeClass("control-on");
            $(this).addClass("control-on");
        }
    };


    return (
        <div>
            <div>
                <input id="NORMAL" type='button' value='일반지도' onClick={btnClick} />
                <input id="TERRAIN" type='button' value='지형도' onClick={btnClick} />
                <input id="SATELLITE" type='button' value='위성지도' onClick={btnClick} />
                <input id="HYBRID" type='button' value='겹쳐보기' onClick={btnClick} />
            </div>
            <div id="naverMap" style={{width:'1000px', height:'500px', position:'relative', overflow:'hidden'}}></div>
        </div>
    )
}

export default NaverMap;
